mapper.select:

SELECT *
FROM t_shopping_member
WHERE member_id = #{member_id}
AND member_pw = #{member_pw}

SELECT IF(COUNT(*) = 1, 'true', 'false')
FROM t_shopping_member
WHERE member_id = #{id}

mapper.insert:

INSERT INTO t_shopping_member(
    member_id, member_pw, member_name, member_gender, member_birth_y, member_birth_m, member_birth_d, member_birth_gn,
    tel1, tel2, tel3, hp1, hp2, hp3, smssts_yn, email1, email2, emailsts_yn, zipcode, roadAddress, jibunAddress, namujiAddress
)
VALUES(
    #{member_id}, #{member_pw}, #{member_name}, #{member_gender}, #{member_birth_y}, #{member_birth_m}, #{member_birth_d}, #{member_birth_gn},
    #{tel1}, #{tel2}, #{tel3}, #{hp1}, #{hp2}, #{hp3}, #{smssts_yn}, #{email1}, #{email2}, #{emailsts_yn}, #{zipcode}, #{roadAddress}, #{jibunAddress}, #{namujiAddress}
)

mapper.select:

SELECT MEMBER_ID
FROM t_shopping_member
WHERE member_name=#{member_name}
AND member_birth_y=#{member_birth_y}
AND member_birth_m=#{member_birth_m}
AND member_birth_d=#{member_birth_d}
AND member_birth_gn=#{member_birth_gn}
AND email1=#{email1}
AND email2=#{email2}


SELECT *
FROM t_shopping_member
WHERE MEMBER_ID=#{member_id}
AND MEMBER_NAME=#{member_name}
AND EMAIL1=#{email1}
AND EMAIL2 LIKE '%' || #{email2} || '%'

mapper.update:

UPDATE t_shopping_member
SET MEMBER_PW = #{user_psw_confirm}
WHERE MEMBER_ID=#{mem_id}
